DROP TABLE IF EXISTS `w_video`;
CREATE TABLE `w_video` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '序号，主键',
  `video_image` varchar(255) DEFAULT '' COMMENT '封面图片',
  `title` varchar(50) DEFAULT '' COMMENT '视频标题',
  `video_url` varchar(255) DEFAULT '' COMMENT '视频地址',
  `video_intro` varchar(1000) DEFAULT NULL COMMENT '视频介绍',
  `click_count` int(10) DEFAULT '0' COMMENT '视频播放量，默认0',
  `user_id` int(10) DEFAULT '0' COMMENT '添加人',
  `add_time` int(8) DEFAULT '0' COMMENT '添加时间',
  `video_time` varchar(255) DEFAULT '' COMMENT '视频时间',
  `sort_number` int(11) DEFAULT '0' COMMENT '排序值，约大越靠前',
  `video_type` int(1) DEFAULT '1' COMMENT '视频等级：1：入门；2：初级；3：中级；4：高级',
  `status` int(1) DEFAULT '1' COMMENT '视频状态:0：不可用；1：可用,默认1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

